package ru.miit.lab_2;import org.jboss.weld.environment.se.Weld;import org.jboss.weld.environment.se.WeldContainer;import ru.miit.lab_2.clients.ClientCipher;public class App {    public static void main(String[] args) {        Weld weld = new Weld();        WeldContainer container = weld.initialize();        String str = "GEEKSFORGttKS";        String keyword = "AYUSH";        ClientCipher clientCipher = container.select(ClientCipher.class).get();        String result = clientCipher.encrypt(str, keyword);        System.out.println("Affine result: " + result);        container.shutdown();    }}